hudson.tasks.Shell.command:
#!/bin/bash sudo /root/bin/az login --service-principal -u $appId --password $passwd --tenant $tenant cd /tmp/ sudo cp /u01/ops/oss/scripts/demoasr/azuredeploy.parameters.json /tmp/demoasr.parameters.json sudo sed -i "s/{recovery_vault_name}/${vaultname}/g" demoasr.parameters.json sudo sed -i "s/{recovery_vault_location}/${region}/g" demoasr.parameters.json sudo sed -i "s/{vnet_location}/${region}/g" demoasr.parameters.json
sudo /root/bin/az group create -l ${region} -n $ResourceName
sudo /root/bin/az group deployment create \ --name ExampleDeployment \ --resource-group $ResourceName \ --template-file /u01/ops/oss/scripts/demoasr/azuredeploy.json \ --parameters @demoasr.parameters.json
