definition.script:
node() {
    stage('Deploying') {
        build job: '/Deploy Initial VMs/', parameters: [string(name: 'appId', value: "${appID}"), password(description: 'Put in the password for the principle account', name: 'passwd', value: "${passwd}"), string(name: 'tenant', value: "${tenant}"), string(name: 'vaultname', value: "${vaultname}"), string(name: 'region', value: "${region}"), string(name: 'ResourceName', value: "${ResourceName}")]
    }
    stage('Waiting to Delete Resources'){
        timeout(time: 7200, unit: 'SECONDS') {
        input "continue without waiting..."
        }
    }
    stage('cleanup'){
        build job: '/Delete_Resources/', parameters: [string(name: 'ResourceName', value: "${ResourceName}")]
    }

}
